mapper.select:
select a.prob_list_id, a.prob_list_num, a.prob_list_name, a.prob_iscompleted, a.prob_list_descrip, a.prob_list_source, a.prob_list_start_time, a.prob_list_end_time, a.prob_list_ATA, a.dept_id, a.prob_list_actual_end_time, a.prob_list_head, a.prob_list_our_head, a.prob_list_status, a.prob_list_evaluation, a.prob_star, a.prob_isoptimize, a.prob_list_clas, a.prob_list_tag, a.prob_list_tips,
f.prob_final_solution_id as sub_prob_final_solution_id, f.prob_process_id as sub_final_prob_process_id, f.prob_final_solution as sub_prob_final_solution, f.prob_MI as sub_prob_MI, f.prob_final_status as sub_prob_final_status,
t.prob_temp_solution_id as sub_prob_temp_solution_id, t.prob_process_id as sub_temp_prob_process_id, t.prob_temp_solution as sub_prob_temp_solution,t.prob_temp_cat as sub_prob_temp_cat,t.prob_temp_file_num as sub_prob_temp_file_num,t.prob_temp_status as sub_prob_temp_status,
from bus_prob_list a
left join bus_prob_final_solution f on f.prob_list_id = a.prob_list_id
left join bus_prob_temp_solution t on t.prob_list_id = a.prob_list_id

     and a.prob_list_num = #{probListNum}
     and a.prob_list_name like concat('%', #{probListName}, '%')
     and a.prob_iscompleted = #{probIscompleted}
     and a.prob_list_source = #{probListSource}
     and a.prob_list_start_time = #{probListStartTime}
     and a.prob_list_end_time = #{probListEndTime}
     and a.prob_list_ATA = #{probListAta}
     and a.dept_id = #{deptId}
     and a.prob_list_actual_end_time = #{probListActualEndTime}
     and a.prob_list_head = #{probListHead}
     and a.prob_list_our_head = #{probListOurHead}
     and a.prob_list_status = #{probListStatus}
     and a.prob_list_evaluation = #{probListEvaluation}
     and a.prob_star = #{probStar}
     and a.prob_isoptimize = #{probIsoptimize}
     and a.prob_list_tag = #{probListTag}

mapper.update:
update bus_prob_list
prob_list_num = #{probListNum}, prob_list_name = #{probListName}, prob_iscompleted = #{probIscompleted}, prob_list_descrip = #{probListDescrip}, prob_list_source = #{probListSource}, prob_list_start_time = #{probListStartTime}, prob_list_end_time = #{probListEndTime}, prob_list_ATA = #{probListAta}, dept_id = #{deptId}, prob_list_actual_end_time = #{probListActualEndTime}, prob_list_head = #{probListHead}, prob_list_our_head = #{probListOurHead}, prob_list_status = #{probListStatus}, prob_list_evaluation = #{probListEvaluation}, prob_star = #{probStar}, prob_isoptimize = #{probIsoptimize}, prob_list_clas = #{probListClas}, prob_list_tag = #{probListTag}, prob_list_tips = #{probListTips},
where prob_list_id = #{probListId}
